import React from 'react';

const Highlights = ({ data }) => {
  const formatTime = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="bg-white shadow rounded-lg p-6 mb-6">
      <h2 className="text-2xl font-bold mb-4">Today's Highlights</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Humidity</p>
          <p className="text-2xl font-semibold">{data.main.humidity}%</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Wind Speed</p>
          <p className="text-2xl font-semibold">{data.wind.speed} m/s</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Pressure</p>
          <p className="text-2xl font-semibold">{data.main.pressure} hPa</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Visibility</p>
          <p className="text-2xl font-semibold">{data.visibility / 1000} km</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Sunrise</p>
          <p className="text-2xl font-semibold">{formatTime(data.sys.sunrise)}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded">
          <p className="text-gray-600">Sunset</p>
          <p className="text-2xl font-semibold">{formatTime(data.sys.sunset)}</p>
        </div>
      </div>
    </div>
  );
};

export default Highlights;
