import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WeatherAlerts = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found, redirecting to login.');
            navigate('/login'); // Redirect to the login page if not logged in
            return; // Exit early to prevent further execution
        }

        try {
            const userData = JSON.parse(atob(token.split('.')[1])); // Decode JWT token payload
            console.log('User data from token:', userData); // Log user data for debugging

            // Fetch complete user details from backend
            const fetchUserDetails = async () => {
                try {
                    const response = await fetch(`/api/users/${userData.id}`, {
                        method: 'GET',
                        headers: {
                            Authorization: `Bearer ${token}`
                        }
                    });

                    if (!response.ok) {
                        throw new Error('Failed to fetch user details');
                    }

                    const userDetails = await response.json(); // Parse the JSON response
                    setUser(userDetails); // Set the complete user object
                } catch (error) {
                    console.error('Error fetching user details:', error);
                    navigate('/login'); // Redirect if there's an error fetching details
                } finally {
                    setLoading(false); // Set loading to false at the end of the check
                }
            };

            fetchUserDetails();
        } catch (error) {
            console.error('Failed to decode token:', error);
            navigate('/login'); // Redirect if there's an error in decoding
        }
    }, [navigate]);

    return (
        <div>
            <h2>Weather Alerts</h2>
            {loading ? (
                <h3>Loading...</h3>
            ) : user ? (
                <h3>Welcome, {user.username}!</h3> // Now this should display the complete user object
            ) : (
                <h3>Redirecting to login...</h3>
            )}
        </div>
    );
};

export default WeatherAlerts;
