import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const history = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/profile', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setUser(data);
        } else {
          throw new Error('Failed to fetch profile');
        }
      } catch (error) {
        console.error('Profile fetch error:', error);
        history.push('/login');
      }
    };

    fetchProfile();
  }, [history]);

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile">
      <h2>User Profile</h2>
      <p>Username: {user.username}</p>
      <p>Email: {user.email}</p>
      <p>Home City: {user.home_city || 'Not set'}</p>
      <h3>Weather Alerts</h3>
      {user.weather_alerts.length > 0 ? (
        <ul>
          {user.weather_alerts.map((alert, index) => (
            <li key={index}>
              {alert.location}: {alert.alert_type} ({alert.threshold_value})
            </li>
          ))}
        </ul>
      ) : (
        <p>No weather alerts set</p>
      )}
      <h3>Recently Searched Locations</h3>
      {user.searched_locations.length > 0 ? (
        <ul>
          {user.searched_locations.map((location, index) => (
            <li key={index}>{location}</li>
          ))}
        </ul>
      ) : (
        <p>No recent searches</p>
      )}
    </div>
  );
};

export default Profile;
