import React, { useEffect, useState } from 'react';
import './WeatherStats.css';

const WeatherStats = () => {
  const [weatherData, setWeatherData] = useState({});

  useEffect(() => {
    const token = localStorage.getItem('token');
    const fetchWeatherStats = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/users/stats`, {
          method: 'GET',
          headers: {
            'x-auth-token': token
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch weather stats');
        }

        const data = await response.json();

        // Group by city and date
        const groupedData = {};
        data.forEach(entry => {
          const city = entry.location;
          const date = new Date(entry.timestamp).toDateString(); // Use the timestamp to get the date

          if (!groupedData[city]) {
            groupedData[city] = [];
          }

          groupedData[city].push({
            ...entry,
            time: new Date(entry.timestamp).toLocaleTimeString() // Add time based on timestamp
          });
        });

        setWeatherData(groupedData);
      } catch (error) {
        console.error('Error fetching weather stats:', error);
      }
    };

    fetchWeatherStats();
  }, []);

  return (
    <div className="container">
      {Object.entries(weatherData).map(([city, entries]) => (
        <div className="city-section" key={city}>
          <h2 className="city-title">{city}</h2>
          {entries.map((details, index) => (
            <div className="weather-card" key={index}>
              <p className="weather-detail">Date: {new Date(details.timestamp).toLocaleDateString()}</p>
              <p className="weather-detail">Time: {details.time}</p>
              <p className="weather-detail">Avg Temperature: {details.averageTemperature} °C</p>
              <p className="weather-detail">Max Temperature: {details.maximumTemperature} °C</p>
              <p className="weather-detail">Min Temperature: {details.minimumTemperature} °C</p>
              <p className="weather-detail">Condition: {details.dominantCondition}</p>
              {details.iconUrl && (
                <img src={`http://openweathermap.org/img/wn/${details.iconUrl}@2x.png`} alt="Weather Icon" />
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default WeatherStats;
