import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WeatherAlerts = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Check if the user is logged in by verifying the JWT token
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login'); // Redirect to the login page if not logged in
        } else {
            // Here you can decode the token or fetch user details from the backend if needed
            // For simplicity, we’ll assume the token contains user information
            // For example, you might decode it with a library like jwt-decode
            const userData = JSON.parse(atob(token.split('.')[1])); // Decode JWT token payload
            setUser(userData);
        }
    }, [navigate]);
    console.log(user);


    return (
        <div>
            <h2>Weather Alerts</h2>
            {user ? (
                <h3>Welcome, {user.user}!</h3> // Display the logged-in user's username
            ) : (
                <h3>Loading...</h3> // You could also show a loading state while checking
            )}
        </div>
    );
};

export default WeatherAlerts;
