// WeatherStats.js
import React, { useEffect, useState } from 'react';
import './WeatherStats.css';

const WeatherStats = () => {
  const [weatherData, setWeatherData] = useState({});

  useEffect(() => {
    const token = localStorage.getItem('token');
    const fetchWeatherStats = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/users/stats`, {
            method: 'GET',
            headers: {
                'x-auth-token': token
            }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch weather stats');
        }

        const data = await response.json();

        // Group by city and date
        const groupedData = {};
        data.forEach(entry => {
          const city = entry.location;
          const date = new Date(entry.timestamp).toDateString(); // Ensure you're using the correct date field

          if (!groupedData[city]) {
            groupedData[city] = {};
          }

          if (!groupedData[city][date]) {
            groupedData[city][date] = entry;
          }
        });

        setWeatherData(groupedData);
      } catch (error) {
        console.error('Error fetching weather stats:', error);
      }
    };

    fetchWeatherStats();
  }, []);

  return (
    <div className="container">
      {Object.entries(weatherData).map(([city, dates]) => (
        <div className="city-section" key={city}>
          <h2 className="city-title">{city}</h2>
          <div className="weather-cards-container">
            {Object.entries(dates).map(([date, details]) => (
              <div className="weather-card" key={date}>
                <p className="weather-detail">Date: {new Date(date).toLocaleDateString()}</p>
                <p className="weather-detail">Avg Temp: {details.averageTemperature} °C</p>
                <p className="weather-detail">Max Temp: {details.maximumTemperature} °C</p>
                <p className="weather-detail">Min Temp: {details.minimumTemperature} °C</p>
                <p className="weather-detail">Condition: {details.dominantCondition}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default WeatherStats;
