import React from 'react';
import clearSkyDay from '../videos/weather/clear_sky_day.mp4';
import clearSkyNight from '../videos/weather/clear_sky_night.mp4';
import cloudyDay from '../videos/weather/cloudy_sky_day.mp4';
import cloudyNight from '../videos/weather/cloudy_sky_night.mp4';
import rain from '../videos/weather/rain.mp4';
import snow from '../videos/weather/snow.mp4';
// import cloud from '../videos/weather/cloud.mp4';


// A component that takes weather description and returns the appropriate video
const BackgroundVideo = ({ weatherCondition, icon }) => {
  // Mapping of weather conditions to videos
  const weatherVideos = {
    'clear sky day': clearSkyDay,
    'clear sky night': clearSkyNight,
    'cloudy day': cloudyDay,
    'cloudy night': cloudyNight,
    'rain': rain,
    'snow': snow,
    // 'thunderstorm': thunderstorm,
    // 'mist': mist,
  };

  // Determine if it's day or night based on the icon
  const timeOfDay = icon.endsWith('d') ? 'day' : 'night';

  // Create the key for matching with weatherVideos
  let videoKey;
  if (weatherCondition.includes('rain')) {
    videoKey = 'rain';
  } else if (weatherCondition.includes('snow')) {
    videoKey = 'snow';
  } else if (weatherCondition.includes('clouds')) {
    videoKey = `cloudy ${timeOfDay}`;
  } else if (weatherCondition.includes('clear sky')) {
    videoKey = `clear sky ${timeOfDay}`; // Default to night for cloudy conditions
  }
  else
  {
    video=''
  }

  // Fetch the video or fallback to a default video
  const videoUrl = weatherVideos[videoKey] || '../videos/default.mp4';
  console.log("Selected video URL:", videoUrl);
  return (

    <video autoPlay loop muted className="background-video">
      <source src={videoUrl} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  );
};

export default BackgroundVideo;
