import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';

const TemperatureGraph = () => {
  const [violationData, setViolationData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchViolations = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('You need to log in to see temperature data.');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://localhost:3001/api/users/violations', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.msg || 'Failed to fetch temperature data');
        }

        const data = await response.json();
        setViolationData(data);
      } catch (err) {
        setError('An error occurred while fetching temperature data. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchViolations();
  }, []);

  // Handle loading and error states
  if (loading) return <div>Loading temperature data...</div>;
  if (error) return <div className="error-message">{error}</div>;

  // Extract data for the chart
  const timestamps = violationData.map(violation => new Date(violation.timestamp).toLocaleString());
  const thresholdValues = violationData.map(violation => violation.thresholdValue);
  const recordedTemps = violationData.map(violation => violation.recordedTemperature);

  // Chart.js data configuration
  const data = {
    labels: timestamps, // X-axis labels (timestamps)
    datasets: [
      {
        label: 'Threshold Temperature',
        data: thresholdValues, // Y-axis data for threshold
        fill: false,
        borderColor: 'red',
        borderWidth: 2,
        tension: 0.1,
      },
      {
        label: 'Recorded Temperature',
        data: recordedTemps, // Y-axis data for recorded temperature
        fill: false,
        borderColor: 'blue',
        borderWidth: 2,
        tension: 0.1,
      },
    ],
  };

  // Chart.js options for appearance and behavior
  const options = {
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Temperature (°C)',
        },
      },
    },
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Temperature Threshold vs Recorded Temperature',
      },
    },
  };

  return (
    <div>
      <h2>Temperature Variation Graph</h2>
      <Line data={data} options={options} />
    </div>
  );
};

export default TemperatureGraph;
