import React from 'react';
import weatherIcons from './WeatherIcons'; // Import the local weather images

const getWeatherImage = (description, timeOfDay) => {
  if (description.includes("clear sky")) {
    return timeOfDay === 'd' ? weatherIcons.clear_sky_day : weatherIcons.clear_sky_night;
  } else if (description.includes("cloudy")) {
    return timeOfDay === 'd' ? weatherIcons.cloudy_day : weatherIcons.cloudy_night;
  } else if (description.includes("rain")) {
    return weatherIcons.rain;
  } else if (description.includes("frost")) {
    return weatherIcons.frost;
  } 
  return null; // No match found
};

const WeeklyForecast = ({ currentWeather, forecastData }) => {
  // Check if currentWeather is defined
  if (!currentWeather) {
    return <div>Loading current weather...</div>; // You can display a loading message or spinner
  }

  const { weather, main } = currentWeather;
  const description = weather[0].description.toLowerCase();
  const icon = weather[0].icon;
  const timeOfDay = icon.slice(-1); // Get 'd' for day or 'n' for night
  const localImage = getWeatherImage(description, timeOfDay); // Get the local image

  const dailyData = forecastData.list.filter((item, index) => index % 8 === 0).slice(0, 7); // Assuming `forecastData` is the 7-day data

  return (
    <div className="bg-white rounded-3xl p-8 mb-8 shadow-lg">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Current Weather</h2>
      <div className="flex flex-col items-center mb-6">
        <img src={localImage || `http://openweathermap.org/img/wn/${icon}@2x.png`} alt={description} className="w-20 h-20" />
        <p className="text-xl font-bold text-gray-800">{Math.round(main.temp)}°C</p>
        <p className="text-sm text-gray-600">{description}</p>
      </div>
      <h2 className="text-3xl font-bold mb-6 text-gray-800">7-Day Forecast</h2>
      <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-7 gap-4">
        {dailyData.map((day, index) => {
          const dayDescription = day.weather[0].description.toLowerCase();
          const dayIcon = day.weather[0].icon;
          const dayTimeOfDay = dayIcon.slice(-1);
          const dayLocalImage = getWeatherImage(dayDescription, dayTimeOfDay);

          return (
            <div key={index} className="bg-gray-100 rounded-2xl p-4 flex flex-col items-center">
              <p className="font-semibold text-gray-700 mb-2">
                {new Date(day.dt * 1000).toLocaleDateString([], { weekday: 'short' })}
              </p>
              <img
                src={dayLocalImage || `http://openweathermap.org/img/wn/${dayIcon}@2x.png`} // Fallback to OpenWeather image
                alt={dayDescription}
                className="w-16 h-16"
              />
              <p className="text-xl font-bold text-gray-800 mt-2">{Math.round(day.main.temp)}°C</p>
              <p className="text-sm text-gray-600">{day.weather[0].main}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WeeklyForecast;
