// AlertForm.js
import React, { useState } from 'react';

const AlertForm = ({ userId }) => {
  const [alertType, setAlertType] = useState('');
  const [thresholdValue, setThresholdValue] = useState('');
  const [location, setLocation] = useState('');
  const [remark, setRemark] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newAlert = {
      location,
      alert_type: alertType,
      threshold_value: thresholdValue ? parseFloat(thresholdValue) : null,
      remark,
    };

    try {
      const response = await fetch(`/api/users/${userId}/alerts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newAlert),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      alert('Alert created successfully!');

      // Reset form
      setAlertType('');
      setThresholdValue('');
      setLocation('');
      setRemark('');
    } catch (error) {
      console.error('Error creating alert:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create New Alert</h3>

      <label>Location:</label>
      <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} required />

      <label>Alert Type:</label>
      <select value={alertType} onChange={(e) => setAlertType(e.target.value)} required>
        <option value="">Select Alert Type</option>
        <option value="temp_high">Temperature High</option>
        <option value="temp_low">Temperature Low</option>
        <option value="wind">Wind Speed</option>
        <option value="rain">Rain</option>
      </select>

      {(alertType === 'temp_high' || alertType === 'temp_low' || alertType === 'wind') && (
        <>
          <label>Threshold Value:</label>
          <input type="number" value={thresholdValue} onChange={(e) => setThresholdValue(e.target.value)} required />
        </>
      )}

      <label>Remark (optional):</label>
      <textarea value={remark} onChange={(e) => setRemark(e.target.value)}></textarea>

      <button type="submit">Create Alert</button>
    </form>
  );
};

export default AlertForm;
