import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WeatherAlerts = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [weatherAlerts, setWeatherAlerts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found, redirecting to login.');
            navigate('/login');
            return;
        }

        const parseJwt = (token) => {
            const base64Url = token.split('.')[1];
            const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
            return JSON.parse(window.atob(base64));
        };

        try {
            const decoded = parseJwt(token);
            const userId = decoded.id; // Get user ID from the token

            const fetchUserDetails = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/api/users/${userId}`, {
                        method: 'GET',
                        headers: {
                            'x-auth-token': token // Use the correct header for your auth
                        }
                    });

                    if (!response.ok) {
                        throw new Error('Failed to fetch user details');
                    }

                    const userDetails = await response.json();
                    setUsername(userDetails.username); // Assuming the user object contains a 'username'
                    setWeatherAlerts(userDetails.weather_alerts || []); // Assuming weather alerts are in the user object
                } catch (error) {
                    console.error('Error fetching user details:', error);
                    navigate('/login'); // Redirect to login on error
                } finally {
                    setLoading(false); // Set loading to false after the fetch
                }
            };

            fetchUserDetails();
        } catch (error) {
            console.error('Failed to decode token:', error);
            navigate('/login');
        }
    }, [navigate]);

    return (
        <div>
            <h2>User Details</h2>
            {username ? (
                <h3>Welcome, {username}!</h3>
            ) : (
                <h3>Loading user information...</h3>
            )}

            <h4>Your Weather Alerts:</h4>
            {loading ? (
                <p>Loading weather alerts...</p>
            ) : weatherAlerts.length > 0 ? (
                <ul>
                    {weatherAlerts.map((alert, index) => (
                        <li key={index}>
                            <strong>Location:</strong> {alert.location} <br />
                            <strong>Threshold:</strong> {alert.threshold_value} <br />
                            <strong>Type:</strong> {alert.alert_type} <br />
                            {alert.remark && <strong>Remark:</strong>} {alert.remark} <br /><br />
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No weather updates found.</p>
            )}
        </div>
    );
};

export default WeatherAlerts;
