import React, { useEffect, useState } from 'react';
import { AlertCircle, PlusCircle, Thermometer, Wind } from 'lucide-react';

const WeatherAlerts = ({ onLogout }) => {
    const [username, setUsername] = useState('');
    const [weatherAlerts, setWeatherAlerts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [alertDetails, setAlertDetails] = useState({
        location: '',
        threshold_value: '',
        alert_type: 'temp_above',
        remark: ''
    });
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found, user should be redirected to login.');
            onLogout();
            return;
        }

        const fetchUserDetails = async () => {
            try {
                const response = await fetch(`/api/users/me`, {
                    method: 'GET',
                    headers: {
                        'x-auth-token': token
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch user details');
                }

                const userDetails = await response.json();
                setUsername(userDetails.username);
                setWeatherAlerts(userDetails.weather_alerts || []);
            } catch (error) {
                console.error('Error fetching user details:', error);
                onLogout();
            } finally {
                setLoading(false);
            }
        };

        fetchUserDetails();
    }, [onLogout]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setAlertDetails((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        try {
            const response = await fetch('/api/users/addAlert', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'x-auth-token': token
                },
                body: JSON.stringify(alertDetails)
            });

            if (!response.ok) {
                throw new Error('Failed to add alert');
            }

            const updatedUser = await response.json();
            setWeatherAlerts(updatedUser.user.weather_alerts);
            setAlertDetails({ location: '', threshold_value: '', alert_type: 'temp_above', remark: '' });
            setError('');
        } catch (error) {
            console.error('Error adding alert:', error);
            setError('Could not add weather alert. Please try again.');
        }
    };

    const getAlertTypeIcon = (type) => {
        switch (type) {
            case 'temp_above':
            case 'temp_below':
                return <Thermometer className="w-6 h-6 text-blue-500" />;
            case 'wind_speed_above':
                return <Wind className="w-6 h-6 text-green-500" />;
            default:
                return <AlertCircle className="w-6 h-6 text-yellow-500" />;
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-6 bg-gray-100 min-h-screen">
            <h1 className="text-3xl font-bold mb-6 text-gray-800">Weather Alerts Dashboard</h1>
            {username && <h2 className="text-xl mb-4 text-gray-700">Welcome, {username}!</h2>}

            <div className="bg-white rounded-lg shadow-md p-6 mb-6">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Your Weather Alerts</h3>
                {loading ? (
                    <p className="text-gray-600">Loading weather alerts...</p>
                ) : weatherAlerts.length > 0 ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {weatherAlerts.map((alert, index) => (
                            <div key={index} className="bg-gray-50 rounded-lg p-4 shadow">
                                <div className="flex items-center mb-2">
                                    {getAlertTypeIcon(alert.alert_type)}
                                    <h4 className="text-lg font-semibold ml-2 text-gray-800">{alert.location}</h4>
                                </div>
                                <p className="text-gray-700"><strong>Threshold:</strong> {alert.threshold_value}</p>
                                <p className="text-gray-700"><strong>Type:</strong> {alert.alert_type.replace(/_/g, ' ')}</p>
                                {alert.remark && <p className="text-gray-700 mt-2"><strong>Note:</strong> {alert.remark}</p>}
                            </div>
                        ))}
                    </div>
                ) : (
                    <p className="text-gray-600">No weather alerts found.</p>
                )}
            </div>

            <div className="bg-white rounded-lg shadow-md p-6">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Add a New Weather Alert</h3>
                <form onSubmit={handleSubmit} className="space-y-4">
                    {error && <p className="text-red-500">{error}</p>}
                    <div>
                        <label htmlFor="location" className="block text-sm font-medium text-gray-700">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={alertDetails.location}
                            onChange={handleInputChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label htmlFor="threshold_value" className="block text-sm font-medium text-gray-700">Threshold Value</label>
                        <input
                            type="number"
                            id="threshold_value"
                            name="threshold_value"
                            value={alertDetails.threshold_value}
                            onChange={handleInputChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                        />
                    </div>
                    <div>
                        <label htmlFor="alert_type" className="block text-sm font-medium text-gray-700">Alert Type</label>
                        <select
                            id="alert_type"
                            name="alert_type"
                            value={alertDetails.alert_type}
                            onChange={handleInputChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                        >
                            <option value="temp_above">Temperature Above</option>
                            <option value="temp_below">Temperature Below</option>
                            <option value="wind_speed_above">Wind Speed Above</option>
                        </select>
                    </div>
                    <div>
                        <label htmlFor="remark" className="block text-sm font-medium text-gray-700">Remark (optional)</label>
                        <input
                            type="text"
                            id="remark"
                            name="remark"
                            value={alertDetails.remark}
                            onChange={handleInputChange}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                        />
                    </div>
                    <button
                        type="submit"
                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                        <PlusCircle className="w-5 h-5 mr-2" />
                        Add Alert
                    </button>
                </form>
            </div>
        </div>
    );
};

export default WeatherAlerts;
