import React, { useState, useEffect } from 'react';
import './AlertForm.css'; // Import the CSS file for styling

const AlertForm = ({ userId }) => {
  // Form states
  const [alertType, setAlertType] = useState('');
  const [thresholdValue, setThresholdValue] = useState('');
  const [location, setLocation] = useState('');
  const [remark, setRemark] = useState('');

  // State for fetching and displaying alerts
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch existing alerts for the user when the component mounts
  useEffect(() => {
    const fetchAlerts = async () => {
      try {
        const response = await fetch(`/api/users/${userId}/alerts`);
        const data = await response.json();
        setAlerts(data); // Store the user's alerts in state
        setLoading(false);
      } catch (error) {
        console.error('Error fetching alerts:', error);
        setLoading(false);
      }
    };

    fetchAlerts();
  }, [userId]);

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const newAlert = {
      location,
      alert_type: alertType,
      threshold_value: thresholdValue ? parseFloat(thresholdValue) : null,
      remark,
    };

    try {
      const response = await fetch(`/api/users/${userId}/alerts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newAlert),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      alert('Alert created successfully!');

      // Reset form
      setAlertType('');
      setThresholdValue('');
      setLocation('');
      setRemark('');

      // Fetch alerts again to show the new one
      const updatedAlerts = await fetch(`/api/users/${userId}/alerts`);
      const data = await updatedAlerts.json();
      setAlerts(data);

    } catch (error) {
      console.error('Error creating alert:', error);
    }
  };

  return (
    <div className="alert-container">
      <form onSubmit={handleSubmit} className="alert-form">
        <h3 className="form-title">Create New Alert</h3>

        <div className="form-group">
          <label htmlFor="location">Location:</label>
          <input
            type="text"
            id="location"
            className="form-control"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="alertType">Alert Type:</label>
          <select
            id="alertType"
            className="form-control"
            value={alertType}
            onChange={(e) => setAlertType(e.target.value)}
            required
          >
            <option value="">Select Alert Type</option>
            <option value="temp_high">Temperature High</option>
            <option value="temp_low">Temperature Low</option>
            <option value="wind">Wind Speed</option>
            <option value="rain">Rain</option>
          </select>
        </div>

        {(alertType === 'temp_high' || alertType === 'temp_low' || alertType === 'wind') && (
          <div className="form-group">
            <label htmlFor="thresholdValue">Threshold Value:</label>
            <input
              type="number"
              id="thresholdValue"
              className="form-control"
              value={thresholdValue}
              onChange={(e) => setThresholdValue(e.target.value)}
              required
            />
          </div>
        )}

        <div className="form-group">
          <label htmlFor="remark">Remark (optional):</label>
          <textarea
            id="remark"
            className="form-control"
            value={remark}
            onChange={(e) => setRemark(e.target.value)}
          ></textarea>
        </div>

        <button type="submit" className="submit-btn">
          Create Alert
        </button>
      </form>

      <hr />

      <h3 className="alert-title">Your Alerts</h3>
      {loading ? (
        <p className="loading-text">Loading alerts...</p>
      ) : (
        <ul className="alert-list">
          {alerts.length > 0 ? (
            alerts.map((alert) => (
              <li key={alert._id} className="alert-item">
                <strong>{alert.alert_type}</strong> for {alert.location} at {alert.threshold_value}.
                <br />
                <em>{alert.remark}</em>
              </li>
            ))
          ) : (
            <p>No alerts set yet.</p>
          )}
        </ul>
      )}
    </div>
  );
};

export default AlertForm;
