const Highlights = ({ data }) => {
    const formatTime = (timestamp) => {
      return new Date(timestamp * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    return (
      <div className="bg-white shadow rounded-lg p-4 mb-4">
        <h2 className="text-2xl font-semibold mb-2">Today's Highlights</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div>
            <p className="text-gray-600">Min/Max Temp</p>
            <p className="text-xl font-semibold">{Math.round(data.main.temp_min)}°C / {Math.round(data.main.temp_max)}°C</p>
          </div>
          <div>
            <p className="text-gray-600">Sunrise/Sunset</p>
            <p className="text-xl font-semibold">{formatTime(data.sys.sunrise)} / {formatTime(data.sys.sunset)}</p>
          </div>
          <div>
            <p className="text-gray-600">Wind Speed</p>
            <p className="text-xl font-semibold">{data.wind.speed} m/s</p>
          </div>
          <div>
            <p className="text-gray-600">Visibility</p>
            <p className="text-xl font-semibold">{data.visibility / 1000} km</p>
          </div>
          <div>
            <p className="text-gray-600">Feels Like</p>
            <p className="text-xl font-semibold">{Math.round(data.main.feels_like)}°C</p>
          </div>
        </div>
      </div>
    );
  };
