import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WeatherAlerts = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found, redirecting to login.');
            navigate('/login');
            return;
        }

        const parseJwt = (token) => {
            const base64Url = token.split('.')[1];
            const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
            return JSON.parse(window.atob(base64));
        };

        try {
            const decoded = parseJwt(token);
            const userId = decoded.id; // Get user ID from the token
            console.log(userId);


            const fetchUserDetails = async () => {
                console.log("hell");

                try {
                    const response = await fetch(`/api/users/${userId}`, {
                        method: 'GET',
                        headers: {
                            Authorization: `Bearer ${token}`    
                        }
                    });

                    if (!response.ok) {
                        throw new Error('Failed to fetch user details');
                    }

                    const userDetails = await response.json();
                    setUser(userDetails);
                } catch (error) {
                    console.error('Error fetching user details:', error);
                    setError('Could not retrieve user details.');
                } finally {
                    setLoading(false);
                }
            };

            fetchUserDetails();
        } catch (error) {
            console.error('Failed to decode token:', error);
            navigate('/login');
        }
    }, [navigate]);

    return (
        <div>
            <h2>User Details</h2>
            {loading ? (
                <h3>Loading user information...</h3>
            ) : user ? (
                <div>
                    <h3>Welcome, {user.username}!</h3>
                    <p><strong>Email:</strong> {user.email}</p>
                    <p><strong>Home City:</strong> {user.home_city}</p>
                    <h4>Your Weather Alerts:</h4>
                    <ul>
                        {user.weather_alerts.length > 0 ? (
                            user.weather_alerts.map((alert, index) => (
                                <li key={index}>
                                    <strong>Location:</strong> {alert.location} <br />
                                    <strong>Threshold:</strong> {alert.threshold_value} <br />
                                    <strong>Type:</strong> {alert.alert_type} <br />
                                    {alert.remark && <strong>Remark:</strong>} {alert.remark} <br /><br />
                                </li>
                            ))
                        ) : (
                            <p>No weather alerts found.</p>
                        )}
                    </ul>
                </div>
            ) : (
                <p>{error}</p>
            )}
        </div>
    );
};

export default WeatherAlerts;
