// components/ViolationList.js

import React, { useEffect, useState } from 'react';

const ViolationList = () => {
  const [violations, setViolations] = useState([]); // State to store fetched violations
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // State to store any potential errors

  useEffect(() => {
    const fetchViolations = async () => {
      const token = localStorage.getItem('token'); // Get token from local storage
      if (!token) {
        setError('You need to log in to see violations.');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://localhost:3001/api/users/violations', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token, // Include token in headers
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch violations.');
        }

        const data = await response.json(); // Parse the JSON response
        setViolations(data); // Set the violations state with the fetched data
      } catch (err) {
        setError(err.message); // Set the error state if there is an error
        console.error(err);
      } finally {
        setLoading(false); // Set loading to false once the fetch is done
      }
    };

    fetchViolations();
  }, []);

  if (loading) return <div>Loading...</div>; // Loading state
  if (error) return <div>{error}</div>; // Error state

  return (
    <div>
      <h2>Your Violations</h2>
      {violations.length === 0 ? (
        <p>No violations recorded.</p>
      ) : (
        <ul>
          {violations.map((violation) => (
            <li key={violation._id}>
              <strong>Location:</strong> {violation.location} <br />
              <strong>Alert Type:</strong> {violation.alertType} <br />
              <strong>Threshold Value:</strong> {violation.thresholdValue} <br />
              <strong>Recorded Temperature:</strong> {violation.recordedTemperature} Â°C <br />
              <strong>Recorded Wind Speed:</strong> {violation.recordedWindSpeed} m/s <br />
              <strong>Timestamp:</strong> {new Date(violation.timestamp).toLocaleString()} <br />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViolationList;
