import React from 'react';

const WeeklyForecast = ({ data }) => {
  const dailyData = data.list.filter((item, index) => index % 8 === 0).slice(0, 7);

  return (
    <div className="bg-white shadow rounded-lg p-6 mb-6">
      <h2 className="text-2xl font-bold mb-4">7-Day Forecast</h2>
      <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-7 gap-4">
        {dailyData.map((day, index) => (
          <div key={index} className="text-center bg-gray-100 p-3 rounded">
            <p className="font-semibold">{new Date(day.dt * 1000).toLocaleDateString([], { weekday: 'short' })}</p>
            <img
              src={`http://openweathermap.org/img/wn/${day.weather[0].icon}.png`}
              alt={day.weather[0].description}
              className="w-12 h-12 mx-auto"
            />
            <p className="text-sm">{Math.round(day.main.temp)}Â°C</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WeeklyForecast;
