import React, { useState, useEffect } from 'react';
import './AlertManager.css'; // Import your CSS file

const AlertManager = ({ userId }) => {
  const [alertData, setAlertData] = useState({
    location: '',
    alert_type: '',
    threshold_value: '',
    triggered_time: new Date(),
    factors: {
      temperature: '',
      wind_speed: '',
      rain: '',
    },
    remark: '',
  });
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    const fetchAlerts = async () => {
      try {
        const response = await fetch(`/api/users/${userId}/alerts`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setAlerts(data);
      } catch (error) {
        console.error('Error fetching alerts:', error);
      }
    };

    fetchAlerts();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('factors.')) {
      const factor = name.split('.')[1];
      setAlertData((prevState) => ({
        ...prevState,
        factors: { ...prevState.factors, [factor]: value },
      }));
    } else {
      setAlertData((prevState) => ({ ...prevState, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/api/users/${userId}/alerts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(alertData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      alert('Alert created successfully!');
      setAlertData({
        location: '',
        alert_type: '',
        threshold_value: '',
        triggered_time: new Date(),
        factors: { temperature: '', wind_speed: '', rain: '' },
        remark: '',
      });

      // Re-fetch alerts after creating a new one
      const fetchResponse = await fetch(`/api/users/${userId}/alerts`);
      if (!fetchResponse.ok) {
        throw new Error(`HTTP error! Status: ${fetchResponse.status}`);
      }
      const data = await fetchResponse.json();
      setAlerts(data);
    } catch (error) {
      console.error('Error creating alert:', error);
    }
  };

  return (
    <div className="alert-manager-container">
      <h2 className="alert-title">Create Alert</h2>
      <form onSubmit={handleSubmit} className="alert-form">
        <input type="text" name="location" placeholder="Location" value={alertData.location} onChange={handleChange} required />
        <input type="text" name="alert_type" placeholder="Alert Type" value={alertData.alert_type} onChange={handleChange} required />
        <input type="number" name="threshold_value" placeholder="Threshold Value" value={alertData.threshold_value} onChange={handleChange} required />
        <input type="datetime-local" name="triggered_time" value={alertData.triggered_time.toISOString().slice(0, 16)} onChange={handleChange} required />
        <input type="number" name="factors.temperature" placeholder="Temperature" value={alertData.factors.temperature} onChange={handleChange} required />
        <input type="number" name="factors.wind_speed" placeholder="Wind Speed" value={alertData.factors.wind_speed} onChange={handleChange} required />
        <input type="number" name="factors.rain" placeholder="Rain" value={alertData.factors.rain} onChange={handleChange} required />
        <textarea name="remark" placeholder="Remark" value={alertData.remark} onChange={handleChange}></textarea>
        <button type="submit" className="create-alert-button">Create Alert</button>
      </form>

      <h2 className="alert-title">Existing Alerts</h2>
      <ul className="alert-list">
        {alerts.map((alert, index) => (
          <li key={index} className="alert-item">
            <p><strong>Location:</strong> {alert.location}</p>
            <p><strong>Alert Type:</strong> {alert.alert_type}</p>
            <p><strong>Threshold Value:</strong> {alert.threshold_value}</p>
            <p><strong>Triggered Time:</strong> {new Date(alert.triggered_time).toLocaleString()}</p>
            <p><strong>Factors:</strong> Temperature: {alert.factors.temperature}, Wind Speed: {alert.factors.wind_speed}, Rain: {alert.factors.rain}</p>
            <p><strong>Remark:</strong> {alert.remark}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlertManager;
