import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const WeatherAlerts = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found, redirecting to login.');
            navigate('/login');
            return;
        }

        const parseJwt = (token) => {
            const base64Url = token.split('.')[1];
            const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
            return JSON.parse(window.atob(base64));
        };

        try {
            const decoded = parseJwt(token);
            const userId = decoded.id; // Get user ID from the token

            const fetchUserDetails = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/api/users/${userId}`, {
                        method: 'GET',
                        headers: {
                            'x-auth-token': token // Use the correct header for your auth
                        }
                    });

                    if (!response.ok) {
                        throw new Error('Failed to fetch user details');
                    }

                    const userDetails = await response.json();
                    setUsername(userDetails.username); // Assuming the user object contains a 'username' field
                } catch (error) {
                    console.error('Error fetching user details:', error);
                    navigate('/login'); // Redirect to login on error
                }
            };

            fetchUserDetails();
        } catch (error) {
            console.error('Failed to decode token:', error);
            navigate('/login');
        }
    }, [navigate]);

    return (
        <div>
            <h2>User Details</h2>
            {username ? (
                <h3>Welcome, {username}!</h3>
            ) : (
                <h3>Loading user information...</h3>
            )}
        </div>
    );
};

export default WeatherAlerts;
