import React, { useState, useEffect } from 'react';

const WeatherAlerts = () => {
    const [alerts, setAlerts] = useState([]);
    const [formData, setFormData] = useState({
        location: '',
        threshold_value: '',
        alert_type: 'temp_above',
        remark: '',
    });
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(true);

    // Assuming you have stored JWT token in localStorage after login
    const token = localStorage.getItem('token');

    // Fetch the existing weather alerts when the component mounts
    useEffect(() => {
        const fetchAlerts = async () => {
            try {
                const response = await fetch('/api/weather-alerts', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });
                const data = await response.json();
                if (!response.ok) {
                    throw new Error(data.error || 'Failed to fetch weather alerts.');
                }
                setAlerts(data.alerts);
            } catch (error) {
                setMessage(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchAlerts();
    }, [token]);

    // Handle input changes for the form
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Submit the form data to add a new weather alert
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/weather-alerts/add-alert', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });
            const data = await response.json();
            if (!response.ok) {
                throw new Error(data.error || 'Error adding alert. Please try again.');
            }
            setAlerts([...alerts, data.newAlert]); // Add the new alert to the list
            setMessage('Weather alert added successfully');
            setFormData({ location: '', threshold_value: '', alert_type: 'temp_above', remark: '' });
        } catch (error) {
            setMessage(error.message);
        }
    };

    return (
        <div>
            <h2>Weather Alerts</h2>

            {/* Display loading message */}
            {loading && <p>Loading weather alerts...</p>}

            {/* Display existing weather alerts */}
            {!loading && alerts.length > 0 && (
                <div>
                    <h3>Existing Alerts</h3>
                    <ul>
                        {alerts.map((alert, index) => (
                            <li key={index}>
                                <strong>Location:</strong> {alert.location} <br />
                                <strong>Threshold:</strong> {alert.threshold_value} <br />
                                <strong>Type:</strong> {alert.alert_type} <br />
                                {alert.remark && <strong>Remark:</strong> } {alert.remark} <br /><br />
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {!loading && alerts.length === 0 && (
                <p>No weather alerts found. Add a new alert below.</p>
            )}

            {/* Form to add a new alert */}
            <div>
                <h3>Add New Alert</h3>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Location:</label>
                        <input
                            type="text"
                            name="location"
                            value={formData.location}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div>
                        <label>Threshold Value (Temperature/Wind Speed):</label>
                        <input
                            type="number"
                            name="threshold_value"
                            value={formData.threshold_value}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div>
                        <label>Alert Type:</label>
                        <select
                            name="alert_type"
                            value={formData.alert_type}
                            onChange={handleChange}
                            required
                        >
                            <option value="temp_above">Temperature Above</option>
                            <option value="temp_below">Temperature Below</option>
                            <option value="wind_speed_above">Wind Speed Above</option>
                        </select>
                    </div>

                    <div>
                        <label>Remark (Optional):</label>
                        <input
                            type="text"
                            name="remark"
                            value={formData.remark}
                            onChange={handleChange}
                        />
                    </div>

                    <button type="submit">Add Alert</button>
                </form>
            </div>

            {/* Display success or error message */}
            {message && <p>{message}</p>}
        </div>
    );
};

export default WeatherAlerts;
