import React, { useState, useEffect } from 'react';
import './AlertManager.css';

const AlertManager = ({ userId }) => {
  const [alertType, setAlertType] = useState('');
  const [thresholdValue, setThresholdValue] = useState('');
  const [location, setLocation] = useState('');
  const [remark, setRemark] = useState('');
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAlerts();
  }, [userId]);

  const fetchAlerts = async () => {
    try {
      const response = await fetch(`/api/users/${userId}/alerts`, {
        headers: {
          'x-auth-token': localStorage.getItem('token'), // Assuming you store the token in localStorage
        },
      });
      if (!response.ok) throw new Error('Failed to fetch alerts');
      const data = await response.json();
      setAlerts(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching alerts:', error);
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newAlert = {
      location,
      alert_type: alertType,
      threshold_value: parseFloat(thresholdValue),
      remark,
      triggered_time: new Date().toISOString(),
      factors: {
        temperature: null,
        wind_speed: null,
        rain: null,
      },
    };

    try {
      const response = await fetch(`/api/users/${userId}/alerts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token'),
        },
        body: JSON.stringify(newAlert),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to create alert');
      }

      alert('Alert created successfully!');
      resetForm();
      fetchAlerts();
    } catch (error) {
      console.error('Error creating alert:', error);
      alert(`Error creating alert: ${error.message}`);
    }
  };

  const resetForm = () => {
    setAlertType('');
    setThresholdValue('');
    setLocation('');
    setRemark('');
  };

  return (
    <div className="alert-container">
      <form onSubmit={handleSubmit} className="alert-form">
        <h3 className="form-title">Create New Alert</h3>

        <div className="form-group">
          <label htmlFor="location">Location:</label>
          <input
            type="text"
            id="location"
            className="form-control"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="alertType">Alert Type:</label>
          <select
            id="alertType"
            className="form-control"
            value={alertType}
            onChange={(e) => setAlertType(e.target.value)}
            required
          >
            <option value="">Select Alert Type</option>
            <option value="temp_high">Temperature High</option>
            <option value="temp_low">Temperature Low</option>
            <option value="wind">Wind Speed</option>
            <option value="rain">Rain</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="thresholdValue">Threshold Value:</label>
          <input
            type="number"
            id="thresholdValue"
            className="form-control"
            value={thresholdValue}
            onChange={(e) => setThresholdValue(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="remark">Remark (optional):</label>
          <textarea
            id="remark"
            className="form-control"
            value={remark}
            onChange={(e) => setRemark(e.target.value)}
          ></textarea>
        </div>

        <button type="submit" className="submit-btn">
          Create Alert
        </button>
      </form>

      <hr />

      <h3 className="alert-title">Your Alerts</h3>
      {loading ? (
        <p className="loading-text">Loading alerts...</p>
      ) : (
        <ul className="alert-list">
          {alerts.length > 0 ? (
            alerts.map((alert) => (
              <li key={alert._id} className="alert-item">
                <strong>{alert.alert_type}</strong> for {alert.location} at {alert.threshold_value}.
                <br />
                <em>{alert.remark}</em>
              </li>
            ))
          ) : (
            <p>No alerts set yet.</p>
          )}
        </ul>
      )}
    </div>
  );
};

export default AlertManager;
