import React from 'react';
import clearSkyDay from './images/weather/clear_sky_day.png';
import clearSkyNight from './images/weather/clear_sky_night.png';
import cloudyDay from './images/weather/cloud_sky_day.png';
import cloudyNight from './images/weather/cloud_sky_night.png';
import rain from './images/weather/rain_day.png';
import snow from './images/weather/snow.png';

const WeeklyForecast = ({ data }) => {
  const dailyData = data.list.filter((item, index) => index % 8 === 0).slice(0, 7);

  // Mapping of images based on weather description and time of day
  const weatherImages = {
    // 'clear sky day': clearSkyDay,
    'clear sky night': 'url_to_clear_sky_night_image',

  };

  return (
    <div className="bg-white rounded-3xl p-8 mb-8 shadow-lg">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">7-Day Forecast</h2>
      <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-7 gap-4">
        {dailyData.map((day, index) => {
          const icon = day.weather[0].icon; // e.g., '01d'
          const description = day.weather[0].description.toLowerCase(); // e.g., 'clear sky'

          // Determine if it's day or night
          const timeOfDay = icon.endsWith('d') ? 'day' : 'night';

          // Create the key
          const weatherKey = `${description} ${timeOfDay}`;
          console.log(weatherKey)

          // Get the image URL from the mapping
          const imageUrl = weatherImages[weatherKey] || `http://openweathermap.org/img/wn/${icon}@2x.png`; // Fallback to OpenWeather image

          return (
            <div key={index} className="bg-gray-100 rounded-2xl p-4 flex flex-col items-center">
              <p className="font-semibold text-gray-700 mb-2">
                {new Date(day.dt * 1000).toLocaleDateString([], { weekday: 'short' })}
              </p>
              <img
                src={imageUrl}
                alt={weatherKey}
                className="w-16 h-16"
              />
              <p className="text-xl font-bold text-gray-800 mt-2">{Math.round(day.main.temp)}Â°C</p>
              <br />
              <p className="text-xl font-bold text-gray-800 mt-2">{description}</p>
              <br />
              <p className="text-sm text-gray-600">{day.weather[0].main}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WeeklyForecast;
