import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css'; // Make sure to create this CSS file for styling

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Loading state to show spinner
  const [error, setError] = useState(''); // Error state for handling fetch issues
  const navigate = useNavigate(); 

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/users/profile', {
          headers: {
            'x-auth-token': localStorage.getItem('token') // Change this line
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data);
        } else {
          setError('Failed to fetch profile');
          navigate('/login'); 
        }
      } catch (error) {
        console.error('Profile fetch error:', error);
        setError('An error occurred while fetching the profile.');
        navigate('/login'); 
      } finally {
        setLoading(false); // Stop loading once the request is complete
      }
    };

    fetchProfile();
  }, [navigate]);

  if (loading) {
    return <div className="loading">Loading profile...</div>; // Show loading spinner
  }

  if (error) {
    return <div className="error-message">{error}</div>; // Show error message
  }

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <div className="profile-details">
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Home City:</strong> {user.home_city || 'Not set'}</p>
      </div>

      <div className="profile-section">
        <h3>Weather Alerts</h3>
        {user.weather_alerts.length > 0 ? (
          <ul className="alert-list">
            {user.weather_alerts.map((alert, index) => (
              <li key={index}>
                <strong>{alert.location}:</strong> {alert.alert_type} (Threshold: {alert.threshold_value})
              </li>
            ))}
          </ul>
        ) : (
          <p>No weather alerts set</p>
        )}
      </div>

      <div className="profile-section">
        <h3>Recently Searched Locations</h3>
        {user.searched_locations.length > 0 ? (
          <ul className="location-list">
            {user.searched_locations.map((location, index) => (
              <li key={index}>{location}</li>
            ))}
          </ul>
        ) : (
          <p>No recent searches</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
