// components/ViolationList.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ViolationList.css'; // Optional: Add styling if needed

const ViolationList = () => {
    const navigate = useNavigate();
    const [violations, setViolations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token'); // Get token from local storage

        console.log('Token:', token); // Log token to see if it's retrieved correctly

        if (!token) {
            console.log('No token found, redirecting to login.');
            navigate('/login'); // Redirect to login if no token
            return;
        }

        const fetchViolations = async () => {
            try {
                const response = await fetch('http://localhost:3001/api/users/violations', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json', // Add content type
                        'x-auth-token': token // Include token in headers
                    }
                });

                if (!response.ok) {
                    const errorData = await response.json(); // Get error response
                    console.error('Fetch error:', errorData); // Log error
                    throw new Error(errorData.msg || 'Failed to fetch violations'); // Use the error message from the response
                }

                const data = await response.json();
                setViolations(data); // Set the violations state
            } catch (error) {
                console.error('Error fetching violations:', error);
                setError('Could not fetch violations. Please try again.'); // Set error state
                navigate('/login'); // Redirect to login on error (if desired)
            } finally {
                setLoading(false); // Set loading to false once fetch is done
            }
        };

        fetchViolations();
    }, [navigate]);

    if (loading) return <div>Loading...</div>; // Loading state
    if (error) return <div className="error-message">{error}</div>; // Error state

    return (
        <div className="violation-list-container">
            <h2>Your Violations</h2>
            {violations.length === 0 ? (
                <p>No violations recorded.</p>
            ) : (
                <ul>
                    {violations.map((violation) => (
                        <li key={violation._id}>
                            <strong>Location:</strong> {violation.location} <br />
                            <strong>Alert Type:</strong> {violation.alertType} <br />
                            <strong>Threshold Value:</strong> {violation.thresholdValue} <br />
                            <strong>Recorded Temperature:</strong> {violation.recordedTemperature} Â°C <br />
                            <strong>Recorded Wind Speed:</strong> {violation.recordedWindSpeed} m/s <br />
                            <strong>Timestamp:</strong> {new Date(violation.timestamp).toLocaleString()} <br />
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ViolationList;
