import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CurrentWeather from './CurrentWeather';
import './Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newCity, setNewCity] = useState('');
  const [weatherData, setWeatherData] = useState({});
  const [unit, setUnit] = useState('metric'); // Default unit is metric (Celsius)
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/users/profile', {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data);
          fetchWeatherData(data.user.searched_locations);
        } else {
          const errorData = await response.json();
          setError(errorData.msg);
          navigate('/login');
        }
      } catch (error) {
        console.error('Profile fetch error:', error);
        setError('An error occurred while fetching the profile.');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const fetchWeatherData = async (locations) => {
    const weatherPromises = locations.map(async (location) => {
      const response = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${location}&appid=e4ba4b05eee9251d041f4745a2222fce&units=${unit}`);
      if (response.ok) {
        return response.json();
      } else {
        console.error(`Error fetching weather for ${location}`);
        return null;
      }
    });

    const results = await Promise.all(weatherPromises);
    const validResults = results.filter(result => result);
    const updatedWeatherData = validResults.reduce((acc, data) => {
      acc[data.name] = data;
      return acc;
    }, {});

    setWeatherData(updatedWeatherData);
  };

  const addCity = async () => {
    if (newCity.trim() === '') return;
    const updatedLocations = [...user.user.searched_locations, newCity.trim()];
    await updateSearchedLocations(updatedLocations);
    setNewCity('');
    fetchWeatherData(updatedLocations);
  };

  const removeCity = async (city) => {
    const updatedLocations = user.user.searched_locations.filter(location => location !== city);
    await updateSearchedLocations(updatedLocations);
    fetchWeatherData(updatedLocations);
  };

  const updateSearchedLocations = async (updatedLocations) => {
    try {
      const response = await fetch('http://localhost:3001/api/users/updateLocations', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ searched_locations: updatedLocations })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUser(updatedUser);
      } else {
        const errorData = await response.json();
        setError(errorData.msg);
      }
    } catch (error) {
      console.error('Error updating searched locations:', error);
      setError('An error occurred while updating the searched locations.');
    }
  };

  const toggleUnit = () => {
    setUnit(prevUnit => (prevUnit === 'metric' ? 'standard' : 'metric')); // Toggle between Celsius and Kelvin
    fetchWeatherData(user.user.searched_locations); // Fetch weather data again after unit change
  };

  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      {user ? (
        <div className="profile-details">
          <p><strong>Username:</strong> {user.user.username || 'Not available'}</p>
          <p><strong>Email:</strong> {user.user.email || 'Not available'}</p>
          <p><strong>Home City:</strong> {user.home_city || 'Not set'}</p>

          <div className="profile-section">
            <h3>Recently Searched Locations</h3>
            <ul className="location-list">
              {user.user.searched_locations && user.user.searched_locations.length > 0 ? (
                user.user.searched_locations.map((location, index) => (
                  <li key={index}>
                    {location}
                    <button onClick={() => removeCity(location)}>Remove</button>
                  </li>
                ))
              ) : (
                <p>No recent searches</p>
              )}
            </ul>

            <input 
              type="text" 
              value={newCity} 
              onChange={(e) => setNewCity(e.target.value)} 
              placeholder="Add new city"
            />
            <button onClick={addCity}>Add City</button>
          </div>

          <div className="profile-section">
            <h3>Weather for Searched Locations</h3>
            <button onClick={toggleUnit}>
              Toggle to {unit === 'metric' ? 'Kelvin' : 'Celsius'}
            </button>
            {user.user.searched_locations && user.user.searched_locations.length > 0 ? (
              <div className="weather-list">
                {user.user.searched_locations.map((location, index) => (
                  weatherData[location] ? (
                    <CurrentWeather key={index} data={weatherData[location]} unit={unit} />
                  ) : (
                    <p key={index}>Weather data for {location} not available.</p>
                  )
                ))}
              </div>
            ) : (
              <p>No weather data available for searched locations.</p>
            )}
          </div>
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
};

export default Profile;
