import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CurrentWeather from './CurrentWeather';
import './Profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [newCity, setNewCity] = useState('');
  const [homeCity, setHomeCity] = useState('');
  const [unit, setUnit] = useState('metric');
  const [isEditing, setIsEditing] = useState(false);
  const [alertCity, setAlertCity] = useState(''); // New state for alert city
  const [thresholdValue, setThresholdValue] = useState(''); // New state for threshold value
  const [alertType, setAlertType] = useState('temperature'); // New state for alert type
  const navigate = useNavigate();
  const apiKey = 'e4ba4b05eee9251d041f4745a2222fce'; // Your OpenWeather API key
  const [weatherData, setWeatherData] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/users/profile', {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data.user);
          setHomeCity(data.user.home_city || '');
          await fetchWeatherData(data.user.searched_locations, unit);
        } else {
          const errorData = await response.json();
          setError(errorData.msg);
          navigate('/login');
        }
      } catch (error) {
        console.error('Profile fetch error:', error);
        setError('An error occurred while fetching the profile.');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate, unit]);

  const fetchWeatherData = async (cities, unit) => {
    if (cities.length > 0) {
      const promises = cities.map((city) =>
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=${unit}&appid=${apiKey}`)
          .then((res) => res.json())
      );
      const weatherResponses = await Promise.all(promises);
      setWeatherData(weatherResponses);
    }
  };

  const addCity = async () => {
    if (!newCity) return;

    try {
      const response = await fetch('http://localhost:3001/api/users/addCity', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ city: newCity })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUser(updatedUser.user);
        setNewCity('');
        await fetchWeatherData([...user.searched_locations, newCity], unit);
      } else {
        const errorData = await response.json();
        setError(errorData.msg);
      }
    } catch (error) {
      console.error('Error adding city:', error);
      setError('An error occurred while adding the city.');
    }
  };

  const updateHomeCity = async () => {
    if (!homeCity) return;

    try {
      const response = await fetch('http://localhost:3001/api/users/updateHomeCity', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ homeCity })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUser(updatedUser.user);
        setIsEditing(false);
      } else {
        const errorData = await response.json();
        setError(errorData.msg);
      }
    } catch (error) {
      console.error('Error updating home city:', error);
      setError('An error occurred while updating the home city.');
    }
  };

  const removeCity = async (city) => {
    try {
      const response = await fetch('http://localhost:3001/api/users/removeCity', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ city })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUser(updatedUser.user);
        await fetchWeatherData(updatedUser.user.searched_locations, unit);
      } else {
        const errorData = await response.json();
        setError(errorData.msg);
      }
    } catch (error) {
      console.error('Error removing city:', error);
      setError('An error occurred while removing the city.');
    }
  };

  const toggleUnit = (newUnit) => {
    setUnit(newUnit);
    fetchWeatherData(user.searched_locations, newUnit);
  };

  // New function to handle adding alerts
  const addAlert = async () => {
    if (!alertCity || !thresholdValue) return;

    try {
      const response = await fetch('http://localhost:3001/api/alerts/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ location: alertCity, threshold_value: thresholdValue, alert_type: alertType })
      });

      if (response.ok) {
        const updatedUser = await response.json();
        setUser(updatedUser.user);
        setAlertCity('');
        setThresholdValue('');
        setAlertType('temperature'); // Reset to default type
      } else {
        const errorData = await response.json();
        setError(errorData.msg);
      }
    } catch (error) {
      console.error('Error adding alert:', error);
      setError('An error occurred while adding the alert.');
    }
  };

  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      {user ? (
        <div className="profile-details">
          <div className="profile-item">
            <strong>Username:</strong> <span>{user.username}</span>
          </div>
          <div className="profile-item">
            <strong>Email:</strong> <span>{user.email}</span>
          </div>
          <div className="profile-item">
            <strong>Home City:</strong>
            {isEditing ? (
              <div className="home-city-edit">
                <input
                  type="text"
                  value={homeCity}
                  onChange={(e) => setHomeCity(e.target.value)}
                  placeholder="Enter your home city"
                  className="home-city-input"
                />
                <button onClick={updateHomeCity} className="button save-button">Save</button>
                <button onClick={() => setIsEditing(false)} className="button cancel-button">Cancel</button>
              </div>
            ) : (
              <div className="home-city-display">
                <span>{homeCity || 'Not set'}</span>
                <button 
                  onClick={() => {
                    setIsEditing(true);
                    setHomeCity(homeCity || '');
                  }} 
                  className="button edit-button"
                >
                  Edit
                </button>
              </div>
            )}
          </div>

          <div className="profile-section">
            <h3>Weather Alerts</h3>
            {user.weather_alerts && user.weather_alerts.length > 0 ? (
              <ul className="alert-list">
                {user.weather_alerts.map((alert, index) => (
                  <li key={index}>
                    <strong>{alert.location}:</strong> {alert.alert_type} (Threshold: {alert.threshold_value})
                  </li>
                ))}
              </ul>
            ) : (
              <p>No weather alerts set</p>
            )}
          </div>

          {/* New section for adding alerts */}
          <div className="profile-section">
            <h3>Add Weather Alert</h3>
            <div className="alert-form">
              <input
                type="text"
                value={alertCity}
                onChange={(e) => setAlertCity(e.target.value)}
                placeholder="City for alert"
                className="alert-input"
              />
              <input
                type="number"
                value={thresholdValue}
                onChange={(e) => setThresholdValue(e.target.value)}
                placeholder="Threshold Value"
                className="alert-input"
              />
              <select
                value={alertType}
                onChange={(e) => setAlertType(e.target.value)}
                className="alert-input"
              >
                <option value="temperature">Temperature</option>
                <option value="humidity">Humidity</option>
                <option value="precipitation">Precipitation</option>
                {/* Add more alert types as needed */}
              </select>
              <button onClick={addAlert} className="button add-alert-button">Add Alert</button>
            </div>
          </div>

          <div className="profile-section">
            <h3>Weather Data</h3>
            {weatherData.length > 0 ? (
              <CurrentWeather weatherData={weatherData} />
            ) : (
              <p>No weather data available</p>
            )}
          </div>

          <div className="profile-section">
            <h3>Add City</h3>
            <div className="add-city-form">
              <input
                type="text"
                value={newCity}
                onChange={(e) => setNewCity(e.target.value)}
                placeholder="Enter city name"
                className="add-city-input"
              />
              <button onClick={addCity} className="button add-city-button">Add City</button>
            </div>
          </div>

          <div className="profile-section">
            <h3>Search Locations</h3>
            <ul className="searched-locations">
              {user.searched_locations.map((city) => (
                <li key={city}>
                  <span>{city}</span>
                  <button onClick={() => removeCity(city)} className="button remove-city-button">Remove</button>
                </li>
              ))}
            </ul>
          </div>

          <div className="profile-section">
            <h3>Change Unit</h3>
            <button onClick={() => toggleUnit('metric')} className="button unit-button">Metric</button>
            <button onClick={() => toggleUnit('imperial')} className="button unit-button">Imperial</button>
          </div>
        </div>
      ) : (
        <p>User not found.</p>
      )}
    </div>
  );
};

export default Profile;
